from botasaurus import request # Asegúrate de que `request` esté importado correctamente desde botasaurus
from botasaurus_requests import Response  # Aquí aseguramos que Response esté correctamente importado

@request(output=None, create_error_logs=False)
def botasaurus_get(req, url: str) -> Response:
    """ General purpose "get" function that uses Botasaurus.
    
    Parameters
    ----------
    req : botasaurus.request.Request
        The request object provided by the botasaurus decorator
    url : str
        The URL to request
        
    Returns
    -------
    botasaurus_requests.response.Response
        The response from the request
    """
    if not isinstance(url, str):
        raise TypeError('`url` must be a string.')
    resp = req.get(url)  # Se hace la solicitud GET utilizando el objeto `req`
    return resp  # Devolvemos la respuesta
